div.script:
function redirectToNextPage() {
const formData = {
student_name: document.querySelector('input[name="student_name"]').value,
mobile_number: document.querySelector('input[name="mobile_number"]').value,
whatsapp_number: document.querySelector('input[name="whatsapp_number"]').value,
batch_name: document.querySelector('input[name="batch_name"]').value,
batch_id: document.querySelector('input[name="batch_id"]').value,
mail_id: document.querySelector('input[name="mail_id"]').value,
address: document.querySelector('input[name="address"]').value,
level_id: document.querySelector('select[name="level_id"]').value,
group: document.querySelector('input[name="group"]:checked') ?
document.querySelector('input[name="group"]:checked').value : null,
course_level: document.querySelector('input[name="course_level"]').value,

};
console.log(formData, 'formData');

// Store formData in session storage
sessionStorage.setItem('registrationForm', JSON.stringify(formData));

// Redirect based on the selected group and course level
if (formData.group) {
let nextPageUrl = '';
if (formData.group === 'full_course' || formData.group === 'group_1' || formData.group ===
'group_2') {
nextPageUrl = '/crash_form/full_course';
} else {
if (formData.course_level === 'ca_inter') {
nextPageUrl = '/crash_form/ca_inter/other_course';
} else if (formData.course_level === 'cma_final') {
nextPageUrl = '/crash_form/cma_final/other_course';
} else if (formData.course_level === 'cma_inter') {
nextPageUrl = '/crash_form/cma_inter/other_course';
} else if (formData.course_level === 'ca_final') {
nextPageUrl = '/crash_form/ca_final/other_course';
} else {
alert("Please select a valid course level.");
return;
}
}
window.location.href = nextPageUrl;
} else {
alert("Please select a group before proceeding.");
}
}

document.addEventListener('DOMContentLoaded', function() {
// Retrieve formData from sessionStorage
const formData = JSON.parse(sessionStorage.getItem('registrationForm'));
if (formData) {
// Populate the student_name field with the stored value
document.querySelector('input[name="student_name"]').value = formData.student_name;
document.querySelector('input[name="mobile_number"]').value = formData.mobile_number;
document.querySelector('input[name="group"]').value = formData.group;
document.querySelector('input[name="whatsapp_number"]').value = formData.whatsapp_number;
document.querySelector('input[name="batch_id"]').value = formData.batch_id;
document.querySelector('input[name="address"]').value = formData.address;
document.querySelector('input[name="mail_id"]').value = formData.mail_id;
document.querySelector('select[name="level_id"]').value = formData.level_id;

}
});

